const CartItem = () => {
  return (
    <div className="p-5 shadow-lg border rounded-md">
      <div className="flex items-center">
        <div className="w-[5rem] h-[5rem] lg:w-[9rem] lg:h-[9rem]">
          <img
            className="w-full h-full object-cover object-top"
            src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS4d194k1DC_y9fL5AeuqS_6gDPcMDgYz1nBg&s"
            alt=""
          />
        </div>
        <div className="ml-5 space-y-1 ">
          <p className="font-semibold">The title of product</p>
          <p className="opacity-60">Size : X, XL , XXL</p>
          <p className="opacity-60 mt-2 ">Seller : Mohamed Kaanoun</p>
          <div>
            
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartItem;
