import React, { useEffect } from "react";
import { Button, IconButton } from "@mui/material";
import RemoveCircleOutlineIcon from "@mui/icons-material/RemoveCircleOutline";
import ControlPointIcon from "@mui/icons-material/ControlPoint";
import { useDispatch, useSelector } from "react-redux";
import { get, removeCartItem, updateCartItem } from "../../../State/Cart/Action";

const CartItem = ({ item }) => {
  const dispatch = useDispatch();
  const { cart } = useSelector((store) => store);
  const handleUpdateCartItem = (num) => {
    const data = {
      data: { quantity: item.quantity + num },
      cartItemId: item.id,
    };
    dispatch(updateCartItem(data));
  };

  const handleRemoveCartItem = () => {
    dispatch(removeCartItem(item.id));
  };

  useEffect(() => {
    dispatch(get());
  }, [dispatch, cart.updateCartItem, cart.deleteCartItem]);

  return (
    <div className="p-5 shadow-lg border rounded-md">
      <div className="flex items-center">
        <div className="w-[5rem] h-[5rem] lg:w-[9rem] lg:h-[9rem]">
          <img
            className="w-full h-full object-cover object-top"
            src={item.product.imageUrl}
            alt=""
          />
        </div>
        <div className="ml-5 space-y-1 ">
          <p className="font-semibold">{item.product.title}</p>
          <p className="opacity-60">Size : {item.size}</p>
          <p className="opacity-60 mt-2 ">Seller : Mohamed Kaanoun</p>
          <div className="flex space-x-5 items-center text-gray-900 pt-6 ">
            <p className="font-semibold">{item.product.discountPrice} MAD</p>
            <p className="opacity-70 line-through">{item.product.price} MAD</p>
            <p className="text-red-600 font-semibold">
              {item.product.discountPercent}% off
            </p>
          </div>
        </div>
      </div>
      <div className="lg:flex items-center lg:space-x-10 pt-4 ">
        <div className="flex items-center space-x-2 ">
          <IconButton
            sx={{ color: "RGB(145, 85, 253)" }}
            onClick={() => handleUpdateCartItem(-1)}
            disabled={item.quantity <= 1}
          >
            <RemoveCircleOutlineIcon />
          </IconButton>
          <span className="py-1 px-7 border rounded-sm">{item.quantity}</span>
          <IconButton
            sx={{ color: "RGB(145, 85, 253)" }}
            onClick={() => handleUpdateCartItem(1)}
          >
            <ControlPointIcon />
          </IconButton>
        </div>
        <div>
          <Button
            onClick={handleRemoveCartItem}
            sx={{ color: "RGB(145, 85, 253)" }}
          >
            remove
          </Button>
        </div>
      </div>
    </div>
  );
};

export default CartItem;
