import React from "react";
import {
  AccountCircle,
  AccountCircleOutlined,
  AttachMoney,
  CurrencyFranc,
  TrendingUp,
} from "@mui/icons-material";
import {
  Card,
  CardContent,
  Typography,
  Button,
  styled,
  Grid,
  Box,
  Avatar,
} from "@mui/material";
import DevicesIcon from "@mui/icons-material/Devices";

const salesData = [
  {
    stats: "111k",
    title: "Sales",
    color: "primary",
    icon: <TrendingUp sx={{ fontSize: "1.75rem" }} />,
  },
  {
    stats: "333k MAD",
    title: "Customers",
    color: "success",
    icon: <AccountCircle sx={{ fontSize: "1.75rem" }} />,
  },
  {
    stats: "222k",
    title: "Products",
    color: "warning",
    icon: <DevicesIcon sx={{ fontSize: "1.75rem" }} />,
  },
  {
    stats: "444k",
    title: "Revenue",
    color: "info",
    icon: <AttachMoney sx={{ fontSize: "1.75rem" }} />,
  },
];

const renderStats = () => {
  return salesData.mpa((item, index) => (
    <Grid item xs={12} sm={3} key={index}>
      <Box
        sx={{
          display: "flex",
          alignContent: "center",
        }}
      >
        <Avatar
          variant="rounded"
          sx={{
            mr: 3,
            width: 44,
            height: 44,
            boxShadow: 3,
            color: "white",
            bgcolor: `${item.color}`,
          }}
        >
          {item.icon}
        </Avatar>
        <Box sx={{ display: "flex", flexDirection: "column" }}>
          <Typography variant="caption">{item.title}</Typography>
          <Typography variant="caption">{item.this.stats}</Typography>
        </Box>
      </Box>
    </Grid>
  ));
};
const MonthlyOverview = () => {
  return (
    <Card
      className=" "
      sx={{ position: "relative", bgcolor: "#242B2E", color: "white" }}
    >
      <CardContent>
        <Typography variant="h6" sx={{ letterSpacing: ".25px" }}>
          Shop With Kaanoun
        </Typography>
        <Typography variant="body2">Congratulations ðŸ¤©</Typography>
        <Typography variant="h5" sx={{ my: 3.1 }}>
          222.2 k
        </Typography>
        <Button size="small" variant="contained">
          View Sales
        </Button>
      </CardContent>
    </Card>
  );
};

export default MonthlyOverview;
